def find_a_b(d):
    if d == 0:
        return (0.0, 0.0)
    if d == 1:
        return None
    phi = (1 + math.sqrt(5)) / 2
    a = (d * phi - d) / (phi ** 2 - 1)
    b = d - a
    return (a, b)

