        return word in self.dictionary

    def find_words(self, word, x, y, visited, found_words):
        if not (0 <= x < self.size and 0 <= y < self.size) or (x, y) in visited:
            return

        visited.add((x, y))
        word += self.board[x][y]

        if self.is_valid_word(word):
            found_words.add(word)

