#include <iostream>

long long gcd(long long a, long long b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

long long lcm(long long a, long long b) {
    return (a * b) / gcd(a, b);
}

int main() {
    long long n, a, b, p, q;
    std::cin >> n >> a >> b >> p >> q;

    long long red_count = n / a;
    long long blue_count = n / b;
    long long common_count = n / lcm(a, b);

    long long total_chocolates = 0;

    if (p > q) {
        total_chocolates = red_count * p + (blue_count - common_count) * q;
    } else {
        total_chocolates = blue_count * q + (red_count - common_count) * p;
    }

    std::cout << total_chocolates << std::endl;

    return 0;
}
