                        ap += correct_count / (i + 1)
                if ground_truth_num == 0:
                    ap_list.append(0.0)
                else:
                    ap_list.append(ap / ground_truth_num)
            return np.mean(ap_list), ap_list
        else:
            return 0.0, [0.0]
