class AccessGatewayFilter:
    def __init__(self):
        pass
    
    def filter(self, request):
        if self.is_start_with('/api') and (request['method'] == 'GET' or request['method'] == 'POST'):
            return True
        else:
            return False
    
    def is_start_with(self, request_uri):
        if request_uri.startswith('/api') or request_uri.startswith('/login'):
            return True
        else:
            return False
    
    def get_jwt_user(self, request):
        try:
            jwt = request['headers']['Authorization']['jwt']
            user = request['headers']['Authorization']['user']
            if datetime.date.today() < datetime.datetime.fromtimestamp(jwt).date():
                return user
            else:
                return None
        except KeyError:
            return None
    
    def set_current_user_info_and_log(self, user):
        with open('access.log', 'a') as logfile:
            logfile.write(f"{datetime.now()} - {user['name']} - {user['address']}\n")
